import BattlePanel from './BattlePanel';
import PokemonPanel from "./PokemonPanel.js";
import React from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class PokemonGame extends React.Component {
	PlayerType = {
		Player: 0,
		Opponent: 1,
	}

	constructor(props) {
		super(props);
		this.handleBattleClick = this.handleBattleClick.bind(this);
		this.state = {
			playerDieResult:{
				val:0,
				timestamp:Date.now()
			},
			opponentDieResult:{
				val:0,
				timestamp:Date.now()
			},
			gameresults:{
				wins:0,
				defeats:0
			}
		}
	}

  handleBattleClick(){
    const player_res = Math.floor(Math.random() * 6) + 1;
    const opponent_res = Math.floor(Math.random() * 6) + 1;
    if (player_res === 6){
        console.log("TODO: Player hit 6")
    }
    if (opponent_res === 6){
        console.log("TODO: Opponent hit 6")
    }
    this.setState({
        playerDieResult:{
					val:player_res,
					timestamp:Date.now()
				},
        opponentDieResult:{
					val:opponent_res,
					timestamp:Date.now()
				}
    })
  };

	notifyBattleLoser = loser => {
		console.log(loser);
		if (loser.state.myType === this.PlayerType.Player){
			alert ('Game Over :( ..');
			this.setState({
				defeats: this.state.gameresults.defeats + 1
			})
		} else if (loser.state.myType === this.PlayerType.Opponent){
			alert ('You Win !');
			this.setState({
				wins: this.state.gameresults.wins + 1
			})
		} else {
			console.log("illegal player type")
		}
	}

  render() {
    return (
			<div className="component-pokemon-game">
				<Row>
					<h1>Pokemon Battle Simulator</h1>
				</Row>
				<Row>
					<Col>
						<PokemonPanel 
							playerType={this.PlayerType.Player} 
							name='Player' 
							dieResult={this.state.opponentDieResult} 
							loserHook={this.notifyBattleLoser} 
						/>
					</Col>
					<Col>
						<BattlePanel 
							handleBattleClick={this.handleBattleClick} 
							playerDieResult={this.state.playerDieResult} 
							opponentDieResult={this.state.opponentDieResult}
						/>
					</Col>
					<Col>
						<PokemonPanel 
							playerType={this.PlayerType.Opponent} 
							name='Opponent' 
							dieResult={this.state.playerDieResult} 
							loserHook={this.notifyBattleLoser}
						/>
					</Col>
				</Row>
				<Row>
					<div className="component-pokemon-stats">
						<div>Wins: {this.state.gameresults.victories}</div>
						<div>Defeats: {this.state.gameresults.defeats}</div>
					</div>
				</Row>
			</div>
    );
  }
}
