import React from "react";
import PokemonHealthPanel from "./PokemonHealthPanel.js";

export default  class PokemonPanel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myHealth:100,
      myName:this.props.name
    };
  };

  componentDidUpdate(previousProps, previousState) {
    console.log("ok");
    if (!previousState.length) {
      console.log("ok2");
      var result = this.state.myHealth - this.props.dieResult;
      console.log(result)
      this.setState({
        myHealth: this.state.myHealth - this.props.dieResult
      })
    }
  }

  render() {
    return (
        <div className="component-pokemon panel">
            <div className="component-pokemon-name">
                {this.state.myName}
            </div>
            <PokemonHealthPanel current_health={this.state.myHealth}/>
            <div className="component-pokemon-image">

            </div>
        </div>
      );
  }
}
