import BattlePanel from './BattlePanel';
import PokemonPanel from "./PokemonPanel.js";
import React from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class PokemonGame extends React.Component {
	constructor(props) {
		super(props);
		this.handleBattleClick = this.handleBattleClick.bind(this);
		this.state = {
			playerDieResult:{
				val:0,
				timestamp:Date.now()
			},
			opponentDieResult:{
				val:0,
				timestamp:Date.now()
			}
		}
	}

  handleBattleClick(){
    console.log("attack button clicked");
    const player_res = this.generateRandom(6);
    const opponent_res = this.generateRandom(6);
    if (player_res === 6){
        console.log("TODO: Player hit 6")
    }
    if (opponent_res === 6){
        console.log("TODO: Opponent hit 6")
    }
    this.setState({
        playerDieResult:{
					val:player_res,
					timestamp:Date.now()
				},
        opponentDieResult:{
					val:opponent_res,
					timestamp:Date.now()
				}
    })
  };

  generateRandom = (max) => {
    return Math.floor(Math.random() * max) + 1
	};

	notifyBattleWinner = text => {
		console.log(text);
		this.setState({winner: text})
	}

  render() {
    return (
			<div className="component-pokemon-game">
				<Row>
					<h1>Pokemon Battle Simulator</h1>
				</Row>
				<Row>
					<Col>
						<PokemonPanel name="Player" dieResult={this.state.opponentDieResult} winHook={this.notifyBattleWinner} />
					</Col>
					<Col>
						<BattlePanel handleBattleClick={this.handleBattleClick} playerDieResult={this.state.playerDieResult} opponentDieResult={this.state.opponentDieResult}/>
					</Col>
					<Col>
						<PokemonPanel name="Opponent" dieResult={this.state.playerDieResult} winHook={this.notifyBattleWinner}/>
					</Col>
				</Row>
			</div>
    );
  }
}
