import React from "react";

export default class BattleButton extends React.Component {

    handleClick = buttonName => {
        this.props.handleBattleClick(buttonName);
    };

    render() {
        return (
            <button onClick={this.handleClick}>
                Attack!
            </button>
        );
    }
}
