import './App.css';
import BattlePanel from './BattlePanel';
import PokemonPanel from "./PokemonPanel.js";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'bootstrap/dist/css/bootstrap.min.css';

class PokemonGame extends React.Component {

  handleBattleClick(){
    console.log("attack button clicked");
    let player_res = this.generateRandom(6);
    let opponent_res = this.generateRandom(6);
    if (player_res === 6){
        console.log("TODO: Player hit 6")
    }
    if (opponent_res === 6){
        console.log("TODO: Opponent hit 6")
    }
    this.setState({
        playerDieResult:player_res,
        opponentDieResult:opponent_res
    })
  };

  render() {
    return (
        <div className="component-pokemon-game">
            <Row>
                <h1>Pokemon Battle Simulator</h1>
            </Row>
            <Row>
                <Col>
                    <PokemonPanel name="Player"/>
                </Col>
                <Col>
                    <BattlePanel/>
                </Col>
                <Col>
                    <PokemonPanel name="Opponent"/>
                </Col>
            </Row>
        </div>
    );
  }
}

export default App;
