import BattlePanel from './BattlePanel';
import PokemonPanel from "./PokemonPanel.js";
import React from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class PokemonGame extends React.Component {
	PlayerType = {
		Player: 0,
		Opponent: 1,
	}

	constructor(props) {
		super(props);
		this.handleBattleClick = this.handleBattleClick.bind(this);
		this.state = {
			playerPokemon:{
				health:100
			},
			opponentPokemon:{
				health:100
			},
			playerDieResult:{
				val:0,
				timestamp:Date.now()
			},
			opponentDieResult:{
				val:0,
				timestamp:Date.now()
			},
			gameresults:{
				wins:0,
				defeats:0
			},
			gameOverFlag:false,
			playerKey:0,
			opponentKey:0
		}
	}

  handleBattleClick(){
    const player_res = Math.floor(Math.random() * 6) + 1;
    const opponent_res = Math.floor(Math.random() * 6) + 1;
    if (player_res === 6){
        console.log("TODO: Player hit 6")
    }
    if (opponent_res === 6){
        console.log("TODO: Opponent hit 6")
    }
    this.setState({
        playerPokemon:{
					health: this.state.playerPokemon.health - player_res,
					last_die_cast: player_res
				},
        opponentPokemon:{
					health: this.state.opponentPokemon.health - opponent_res,
					last_die_cast: opponent_res
				}
    })
  };

	notifyBattleLoser = loser => {
		this.setState({gameOverFlag:true});
		if (loser.state.myType === this.PlayerType.Player){
			alert ('Game Over :( ..');
			this.setState({
				gameresults: {
					defeats: this.state.gameresults.defeats + 1,
					wins: this.state.gameresults.wins,
				}
			})
		} else if (loser.state.myType === this.PlayerType.Opponent){
			alert ('You Win !');
			this.setState({
				gameresults: {
					wins: this.state.gameresults.wins + 1,
					defeats: this.state.gameresults.defeats
				}
			})
		} else {
			console.log("illegal player type")
		}
	}

	restartGame = condition => {
		if (condition === "new"){
			this.setState({
				playerKey: this.state.playerKey + 1,
				gameresults: {
					wins: 0,
					defeats: 0
				}
			});
		}
		this.setState({
			gameOverFlag:false,
			opponentKey: this.state.opponentKey + 1
		});
	}

  render() {
    return (
			<div className="component-pokemon-game">
				<Row>
					<h1>Pokemon Battle Simulator</h1>
				</Row>
				<Row>
					<div className={this.state.gameOverFlag? 'fadeMe' : 'fadeMe hidden'}>
						<button onClick={(e) => this.restartGame("new", e)}>New Pokemon</button>
						<button onClick={(e) => this.restartGame("same", e)}>Same Pokemon</button>
					</div>
					<Col>
						<PokemonPanel 
							key={this.state.playerKey}
							pokemon={this.state.playerPokemon}
							playerType={this.PlayerType.Player} 
							name='Player' 
							dieResult={this.state.opponentDieResult} 
							loserHook={this.notifyBattleLoser} 
						/>
					</Col>
					<Col>
						<BattlePanel 
							handleBattleClick={this.handleBattleClick} 
							playerDieResult={this.state.playerDieResult} 
							opponentDieResult={this.state.opponentDieResult}
						/>
					</Col>
					<Col>
						<PokemonPanel
							key={this.state.opponentKey}
							pokemon={this.state.opponentPokemon}
							playerType={this.PlayerType.Opponent} 
							name='Opponent' 
							dieResult={this.state.playerDieResult} 
							loserHook={this.notifyBattleLoser}
						/>
					</Col>
				</Row>
				<Row>
					<div className="component-pokemon-stats">
						<div>Wins: {this.state.gameresults.wins}</div>
						<div>Defeats: {this.state.gameresults.defeats}</div>
					</div>
				</Row>
			</div>
    );
  }
}
