import './BattlePanel.css';
import React from "react";

import BattleDie from "./BattleDie.js";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class BattlePanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playerDieResult:1,
            opponentDieResult:1
        }
      }

    handleBattleClick = buttonName => {
        console.log("attack button clicked");
        let player_res = this.generateRandom(6);
        let opponent_res = this.generateRandom(6);
        if (player_res === '6'){
            console.log("TODO: Player hit 6")
        }
        if (opponent_res === '6'){
            console.log("TODO: Opponent hit 6")
        }
        this.setState({
            playerDieResult:player_res,
            opponentDieResult:opponent_res
        })
    };

    // TODO: Typesafety
    generateRandom = (max) => {
        return Math.floor(Math.random() * max) + 1
      };

  render() {
    return (
        <div className="component-battle-panel">
            <Row>
                <Col>
                    <BattleDie name="player-die" value={this.state.playerDieResult}/>
                </Col>
                <Col>
                    <BattleDie name="opponent-die" value={this.state.opponentDieResult}/>
                </Col>
            </Row>
            <div className="text-result-player">
                You hit for {this.state.playerDieResult}
            </div>
            <div className="text-result-opponent">
                Your opponent hit for {this.state.opponentDieResult}
            </div>
            <button onClick={this.handleBattleClick}>
                Attack!
            </button>
        </div>
      );
  }
}
