import './BattlePanel.css';
import React from "react";

import BattleDie from "./BattleDie.js";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class BattlePanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playerDieResult:1,
            opponentDieResult:1
        }
      }

    handleBattleClick = buttonName => {
        console.log("attack button clicked");
        this.setState({
            playerDieResult:generateRandom(5),
            opponentDieResult:generateRandom(5)
        })
    };

    generateRandom = (max) => {
        return Math.floor(Math.random() * max) + 1
      };

  render() {
    return (
        <div className="component-battle-panel">
            <Row>
                <Col>
                    <BattleDie name="player-die" value={this.state.playerDieResult}/>
                </Col>
                <Col>
                    <BattleDie name="opponent-die" value={this.state.opponentDieResult}/>
                </Col>
            </Row>
            <div className="text-result-player">

            </div>
            <div className="text-result-opponent">

            </div>
            <button onClick={this.handleBattleClick}>
                Attack!
            </button>
        </div>
      );
  }
}
