import React from "react";
import PokemonHealthPanel from "./PokemonHealthPanel.js";

export default  class PokemonPanel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myHealth:100,
      myName:this.props.name,
      myType:this.props.playerType,
      myImage:''
    };
  };

  componentDidMount() {
		let randomPokemonId = Math.floor(Math.random() * 500)
    fetch(`https://pokeapi.co/api/v2/pokemon/${randomPokemonId}`)
      .then(res => res.json())
      .then(
				(result) => {
          this.setState({
              myName: result.name,
							myImage: result.sprites.other.dream_world.front_default
					});
        },
			)
  }

  componentDidUpdate(previousProps, previousState) {
    if (previousProps.dieResult !== this.props.dieResult) {
      var res = this.state.myHealth - this.props.dieResult.val
      if (res <= 0){
        res = 0;
        this.props.loserHook(this.state.myType)
      }
      this.setState({
        myHealth: res
      })
    }
  }

  componentDidUnmount() {
    this.state = {
      myHealth:100,
      myName:'',
      myType:'',
      myImage:''
    };
  }

  render() {
    return (
        <div className="component-pokemon panel">
            <div className="component-pokemon-name">
                {this.state.myName}
            </div>
            <PokemonHealthPanel current_health={this.state.myHealth}/>
            <div className="component-pokemon-image">
            <img
              src={this.state.myImage}
              className='img-fluid rounded'
              alt={this.state.myHealth}
            />
            </div>
        </div>
      );
  }
}
